//--------------------------------------------------------------------------------------------------
// Trigger: Button: Confirm Kick Player
//--------------------------------------------------------------------------------------------------
bool gt_ButtonConfirmKickPlayer_Func (bool testConds, bool runActions) {
    // Variable Declarations
    int lv_intPlayer;
    int lv_intSelectedPlayer;
    int lv_intMinimumVotes;
    int lv_intVotesRemaining;
    text lv_txtMessage;

    // Automatic Variable Declarations
    // Variable Initialization
    lv_intPlayer = EventPlayer();

    // Conditions
    if (testConds) {
        if (!(((gv_recGameState.lv_intCurrentGameState == 1) || (gv_recGameState.lv_intCurrentGameState == 3)))) {
            return false;
        }
    }

    // Actions
    if (!runActions) {
        return true;
    }

    lv_intSelectedPlayer = gv_recPlayers[lv_intPlayer].lv_intPlayerKickList[DialogControlGetSelectedItem(gv_recUI.lv_diKickPlayerPulldown, lv_intPlayer)];
    if ((gv_recPlayers[lv_intPlayer].lv_boolPlayersIWantKicked[lv_intSelectedPlayer] == false)) {
        gv_recPlayers[lv_intPlayer].lv_boolPlayersIWantKicked[lv_intSelectedPlayer] = true;
        gv_recPlayers[lv_intSelectedPlayer].lv_intKickVotesAgainstMe += 1;
        lv_intMinimumVotes = gf_getMajorityOfVoters();
        if ((lv_intMinimumVotes > 4)) {
            lv_intMinimumVotes = 4;
        }

        lv_intVotesRemaining = (lv_intMinimumVotes - gv_recPlayers[lv_intSelectedPlayer].lv_intKickVotesAgainstMe);
        if ((lv_intVotesRemaining == 0)) {
            if ((gv_recGlobals.lv_intDebugPlayer == lv_intSelectedPlayer)) {
                lv_txtMessage = (TextWithColor(PlayerName(lv_intSelectedPlayer), libNtve_gf_ConvertPlayerColorToColor(PlayerGetColorIndex(lv_intSelectedPlayer, false))) + StringExternal("Param/Value/2EAA4817"));
                gf_CreateForcedSystemMessage(lv_txtMessage);
            }
            else {
                gv_recPlayers[lv_intSelectedPlayer].lv_boolKicked = true;
                GameOver(lv_intSelectedPlayer, c_gameOverTie, false, true);
                lv_txtMessage = (TextWithColor(PlayerName(lv_intSelectedPlayer), libNtve_gf_ConvertPlayerColorToColor(PlayerGetColorIndex(lv_intSelectedPlayer, false))) + StringExternal("Param/Value/DF07F1C4"));
                gf_CreateForcedSystemMessage(lv_txtMessage);
            }
        }
        else {
            if ((lv_intVotesRemaining > 0)) {
                lv_txtMessage = (TextWithColor(PlayerName(lv_intPlayer), libNtve_gf_ConvertPlayerColorToColor(PlayerGetColorIndex(lv_intPlayer, false))) + StringExternal("Param/Value/C4D3F678") + TextWithColor(PlayerName(lv_intSelectedPlayer), libNtve_gf_ConvertPlayerColorToColor(PlayerGetColorIndex(lv_intSelectedPlayer, false))) + StringExternal("Param/Value/EB2F46A8") + gf_formatIntegerAsText(lv_intVotesRemaining) + StringExternal("Param/Value/815B8E83"));
                if ((lv_intVotesRemaining > 1)) {
                    lv_txtMessage = (lv_txtMessage + StringExternal("Param/Value/4D06C826"));
                }

                lv_txtMessage = (lv_txtMessage + StringExternal("Param/Value/8A4A911C"));
                gf_CreateForcedSystemMessage(lv_txtMessage);
            }

        }
        DialogSetVisible(gv_recUI.lv_dKickPlayerDialog, PlayerGroupSingle(lv_intPlayer), false);
        DialogSetVisible(gv_recUI.lv_dOptionTabsDialog, PlayerGroupSingle(lv_intPlayer), false);
    }

    return true;
}

