//--------------------------------------------------------------------------------------------------
// Trigger: Chat: Any message
//--------------------------------------------------------------------------------------------------
bool gt_ChatAnymessage_Func (bool testConds, bool runActions) {
    // Variable Declarations
    bool lv_boolSpecialCommandEntered;
    int lv_intPlayer;
    string lv_strChatString;
    text lv_txtMessage;
    text lv_txtTemp;
    string lv_strChar;
    string lv_strPetName;
    int lv_intPetIndex;
    string lv_strFirstChars;
    string lv_strFirstChars2;
    bool lv_boolError;
    bool lv_boolValidCommand;
    int lv_intTmpPlayer;
    int lv_intStartPosition;
    int lv_intTmpVal;

    // Automatic Variable Declarations
    // Variable Initialization
    lv_intPlayer = EventPlayer();
    lv_strChatString = EventChatMessage(false);
    lv_strChar = "";
    lv_strPetName = "";
    lv_strFirstChars = "";
    lv_strFirstChars2 = "";

    // Conditions
    if (testConds) {
        if (!((gv_recGameState.lv_boolGameInitComplete == true))) {
            return false;
        }
    }

    // Actions
    if (!runActions) {
        return true;
    }

    if ((StringReplaceWord(lv_strChatString, " ", "", c_stringReplaceAll, c_stringNoCase) != "")) {
        if (((gv_recPlayers[lv_intPlayer].lv_boolSilenced == true) && (lv_intPlayer != gv_recGlobals.lv_intDebugPlayer))) {
            gf_CreateChatMessage(0, (StringExternal("Param/Value/EFD32C45")), lv_intPlayer, false, false);
        }
        else {
            if ((StringSub(lv_strChatString, 1, 1) == "@")) {
                lv_boolSpecialCommandEntered = true;
                lv_intTmpPlayer = StringToInt(StringSub(lv_strChatString, 2, 3));
                if ((lv_intTmpPlayer >= 10)) {
                    lv_intStartPosition = 5;
                }
                else {
                    lv_intStartPosition = 4;
                }
                lv_txtMessage = StringToText(gf_replaceSpecialChars(StringSub(lv_strChatString, lv_intStartPosition, 255)));
                if (((lv_intTmpPlayer <= 0) || (lv_intTmpPlayer == lv_intPlayer) || (lv_intTmpPlayer > gv_recGlobals.lv_iNT_NUM_PLAYERS) || (gv_recPlayers[lv_intTmpPlayer].lv_boolInactive == true))) {
                    gf_CreateChatMessage(0, StringExternal("Param/Value/DB1EFB04"), lv_intPlayer, false, false);
                }
                else {
                    if (((gv_recGameState.lv_boolVotableAllowChat == true) || (gv_recPlayers[lv_intTmpPlayer].lv_boolPlayingMatch == false) || (gv_recPlayers[lv_intPlayer].lv_boolPlayingMatch == true) || (gv_recPlayers[lv_intPlayer].lv_boolIsCommentator == true) || (gv_recGlobals.lv_intDebugPlayer == lv_intPlayer))) {
                        gf_CreateChatMessage(lv_intPlayer, lv_txtMessage, lv_intTmpPlayer, false, false);
                    }
                    else {
                        gf_CreateChatMessage(0, StringExternal("Param/Value/73DF0EC3"), lv_intPlayer, false, false);
                    }
                }
            }
            else {
                if (((StringSub(lv_strChatString, 1, 1) == ".") && (StringSub(lv_strChatString, 2, 2) != "."))) {
                    lv_boolSpecialCommandEntered = true;
                    lv_strFirstChars = StringSub(lv_strChatString, 1, 9);
                    lv_strFirstChars2 = StringSub(lv_strChatString, 1, 8);
                    if (((StringSub(lv_strChatString, 1, 5) == ".all ") || (StringSub(lv_strChatString, 1, 5) == ".ALL ") || (StringSub(lv_strChatString, 1, 3) == ".a "))) {
                        lv_boolValidCommand = true;
                        lv_txtMessage = StringToText(StringSub(lv_strChatString, 6, 255));
                        if ((gv_recPlayers[lv_intPlayer].lv_boolPlayingMatch == true)) {
                            gf_CreateChatMessage(lv_intPlayer, lv_txtMessage, 0, true, false);
                        }
                        else {
                            gf_CreateChatMessage(0, StringExternal("Param/Value/1B72F6CC"), lv_intPlayer, false, false);
                        }
                    }
                    else if (((lv_strChatString == ".pethome") || (lv_strChatString == ".PETHOME"))) {
                        lv_boolValidCommand = true;
                        gf_MakePetsReturnHomeforPlayer(lv_intPlayer);
                    }
                    else if (((StringSub(lv_strChatString, 1, 10) == ".petspeak ") || (StringSub(lv_strChatString, 1, 10) == ".PETSPEAK "))) {
                        lv_boolValidCommand = true;
                        gf_MakePetUtterXforPlayer(lv_intPlayer, StringSub(lv_strChatString, 11, 255));
                    }
                    else if (((lv_strFirstChars == ".petname1") || (lv_strFirstChars == ".PETNAME1") || (lv_strFirstChars == ".petname2") || (lv_strFirstChars == ".PETNAME2"))) {
                        lv_boolValidCommand = true;
                        lv_intPetIndex = StringToInt(StringSub(lv_strChatString, 9, 9));
                        if (((lv_intPetIndex <= 0) || (lv_intPetIndex > 2))) {
                            lv_intPetIndex = 1;
                        }

                        lv_strPetName = StringSub(lv_strChatString, 11, 24);
                        gf_RenamePlayerPet(lv_intPlayer, lv_intPetIndex, lv_strPetName);
                    }
                    else if (((lv_strFirstChars2 == ".petname") || (lv_strFirstChars2 == ".PETNAME"))) {
                        lv_boolValidCommand = true;
                        lv_intPetIndex = 1;
                        lv_strPetName = StringSub(lv_strChatString, 10, 23);
                        gf_RenamePlayerPet(lv_intPlayer, lv_intPetIndex, lv_strPetName);
                    }
                    else if ((lv_strChatString == ".dance")) {
                        lv_boolValidCommand = true;
                        if ((gv_recPlayers[lv_intPlayer].lv_uFaceoffUnit != null)) {
                            libNtve_gf_PlayAnimation(libNtve_gf_MainActorofUnit(gv_recPlayers[lv_intPlayer].lv_uFaceoffUnit), c_animNameDefault, "Dance", 0, c_animTimeDefault);
                        }

                    }
                    else if ((lv_strChatString == ".cheer")) {
                        lv_boolValidCommand = true;
                        if ((gv_recPlayers[lv_intPlayer].lv_uFaceoffUnit != null)) {
                            libNtve_gf_PlayAnimation(libNtve_gf_MainActorofUnit(gv_recPlayers[lv_intPlayer].lv_uFaceoffUnit), c_animNameDefault, "Victory", 0, c_animTimeDefault);
                        }

                    }
                    else if ((lv_strChatString == ".savebank")) {
                        lv_boolValidCommand = true;
                        gf_SaveBankforPlayer(lv_intPlayer);
                        gf_CreateChatMessage(0, StringExternal("Param/Value/ACBD754F"), lv_intPlayer, false, false);
                    }
                    else if ((lv_strChatString == ".loadbank")) {
                        lv_boolValidCommand = true;
                        gf_LoadBankforPlayer(lv_intPlayer);
                        gf_RefreshLeaderboardforPlayer(lv_intPlayer, 2, false);
                        gf_RefreshAvatarButtonsforPlayer(lv_intPlayer);
                        gf_RefreshStoreDialogforPlayer(lv_intPlayer);
                        gf_RefreshPetUIforPlayer(lv_intPlayer);
                        gf_RefreshTurfMarkerUIforPlayer(lv_intPlayer);
                        gf_RefreshTurfSymbolUIforPlayer(lv_intPlayer);
                        gf_CreateChatMessage(0, StringExternal("Param/Value/456A1FB6"), lv_intPlayer, false, false);
                    }
                    else if ((lv_strChatString == ".nodebug")) {
                        lv_boolValidCommand = true;
                        gv_recGlobals.lv_intDebugPlayer = -1;
                        gf_CreateChatMessage(0, StringExternal("Param/Value/E53E65B4"), lv_intPlayer, false, false);
                    }
                    else if ((lv_strChatString == ".shutofflogevents")) {
                        lv_boolValidCommand = true;
                        gv_recGlobals.lv_boolDebugShutoffLogEvents = true;
                        gf_CreateChatMessage(0, StringExternal("Param/Value/35466E4D"), lv_intPlayer, false, false);
                    }
                    else if ((lv_strChatString == ".turnonlogevents")) {
                        lv_boolValidCommand = true;
                        gv_recGlobals.lv_boolDebugShutoffLogEvents = false;
                        gf_CreateChatMessage(0, StringExternal("Param/Value/12D88535"), lv_intPlayer, false, false);
                    }
                    else if ((lv_strChatString == ".shutoffcamevents")) {
                        lv_boolValidCommand = true;
                        gv_recGlobals.lv_boolDebugShutoffCameraEvents = true;
                        gf_CreateChatMessage(0, StringExternal("Param/Value/F66E9038"), lv_intPlayer, false, false);
                    }
                    else if ((lv_strChatString == ".turnoncamevents")) {
                        lv_boolValidCommand = true;
                        gv_recGlobals.lv_boolDebugShutoffCameraEvents = false;
                        gf_CreateChatMessage(0, StringExternal("Param/Value/37AF3246"), lv_intPlayer, false, false);
                    }
                    else if (((StringSub(lv_strChatString, 1, 6) == ".prank") && ((gv_recGlobals.lv_boolDebugMode == true) || (gv_recGlobals.lv_intDebugPlayer == lv_intPlayer)))) {
                        lv_boolValidCommand = true;
                        gf_RunPrankfromPlayer(lv_intPlayer, StringToInt(StringSub(lv_strChatString, 7, 10)));
                    }
                    else if (((lv_strChatString == ".cp") && ((gv_recGlobals.lv_boolDebugMode == true) || (gv_recGlobals.lv_intDebugPlayer == lv_intPlayer)))) {
                        lv_boolValidCommand = true;
                        gf_ClearAllPrankEntities();
                    }
                    else if (((StringSub(lv_strChatString, 1, 9) == ".peepcash") && ((gv_recGlobals.lv_boolDebugMode == true) || (gv_recGlobals.lv_intDebugPlayer == lv_intPlayer)))) {
                        lv_boolValidCommand = true;
                        lv_intTmpVal = StringToInt(StringSub(lv_strChatString, 11, 30));
                        gv_recPlayers[lv_intPlayer].lv_intBankChipsInAccount += lv_intTmpVal;
                        gf_RefreshStoreDialogforPlayer(lv_intPlayer);
                        gf_RefreshDashboardforPlayer(lv_intPlayer);
                        gf_CreateChatMessage(0, (StringExternal("Param/Value/FFFA4F68") + gf_formatIntegerAsText(lv_intTmpVal)), lv_intPlayer, false, false);
                    }
                    else if (((lv_strChatString == ".peepchips") && ((gv_recGlobals.lv_boolDebugMode == true) || (gv_recGlobals.lv_intDebugPlayer == lv_intPlayer)))) {
                        lv_boolValidCommand = true;
                        TriggerExecute(gt_DEVETSTMorechips, true, false);
                    }
                    else if (((lv_strChatString == ".dummies") && ((gv_recGlobals.lv_boolDebugMode == true) || (gv_recGlobals.lv_intDebugPlayer == lv_intPlayer)))) {
                        lv_boolValidCommand = true;
                        if ((gv_recGlobals.lv_boolDebugMode == true)) {
                            gf_CreateDummyPlayers();
                        }

                    }
                    else if (((StringSub(lv_strChatString, 1, 6) == ".kick ") && ((gv_recGlobals.lv_boolDebugMode == true) || (gv_recGlobals.lv_intDebugPlayer == lv_intPlayer)))) {
                        lv_boolValidCommand = true;
                        lv_intTmpPlayer = StringToInt(StringSub(lv_strChatString, 7, 9));
                        gv_recPlayers[lv_intTmpPlayer].lv_boolKicked = true;
                        GameOver(lv_intTmpPlayer, c_gameOverTie, false, true);
                        gf_CreateChatMessage(0, (TextWithColor(PlayerName(lv_intTmpPlayer), libNtve_gf_ConvertPlayerColorToColor(PlayerGetColorIndex(lv_intTmpPlayer, false))) + StringExternal("Param/Value/EF99886B")), 0, false, false);
                    }
                    else if (((StringSub(lv_strChatString, 1, 6) == ".kill ") && ((gv_recGlobals.lv_boolDebugMode == true) || (gv_recGlobals.lv_intDebugPlayer == lv_intPlayer)))) {
                        lv_boolValidCommand = true;
                        gf_RemovePlayerfromMatch(StringToInt(StringSub(lv_strChatString, 7, 9)));
                    }
                    else if (((lv_strChatString == ".alliance") && ((gv_recGlobals.lv_boolDebugMode == true) || (gv_recGlobals.lv_intDebugPlayer == lv_intPlayer)))) {
                        lv_boolValidCommand = true;
                        gf_CreateChatMessage(0, StringExternal("Param/Value/F85D4DD8"), lv_intPlayer, false, false);
                        gf_SetTeamAlliancesVision();
                    }
                    else if (((StringSub(lv_strChatString, 1, 9) == ".silence ") && ((gv_recGlobals.lv_boolDebugMode == true) || (gv_recGlobals.lv_intDebugPlayer == lv_intPlayer)))) {
                        lv_boolValidCommand = true;
                        lv_intTmpPlayer = StringToInt(StringSub(lv_strChatString, 10, 12));
                        gv_recPlayers[lv_intTmpPlayer].lv_boolSilenced = true;
                        gf_CreateChatMessage(0, (TextWithColor(PlayerName(lv_intTmpPlayer), libNtve_gf_ConvertPlayerColorToColor(PlayerGetColorIndex(lv_intTmpPlayer, false))) + StringExternal("Param/Value/F7242E13")), lv_intPlayer, false, false);
                        gf_CreateChatMessage(0, StringExternal("Param/Value/AC97A2AB"), lv_intTmpPlayer, false, false);
                    }
                    else if (((StringSub(lv_strChatString, 1, 11) == ".unsilence ") && ((gv_recGlobals.lv_boolDebugMode == true) || (gv_recGlobals.lv_intDebugPlayer == lv_intPlayer)))) {
                        lv_boolValidCommand = true;
                        lv_intTmpPlayer = StringToInt(StringSub(lv_strChatString, 12, 14));
                        gv_recPlayers[lv_intTmpPlayer].lv_boolSilenced = false;
                        gf_CreateChatMessage(0, (TextWithColor(PlayerName(lv_intTmpPlayer), libNtve_gf_ConvertPlayerColorToColor(PlayerGetColorIndex(lv_intTmpPlayer, false))) + StringExternal("Param/Value/A9F647C2")), lv_intPlayer, false, false);
                        gf_CreateChatMessage(0, StringExternal("Param/Value/5F15A53B"), lv_intTmpPlayer, false, false);
                    }
                    else if (((StringSub(lv_strChatString, 1, 7) == ".prove ") && ((gv_recGlobals.lv_boolDebugMode == true) || (gv_recGlobals.lv_intDebugPlayer == lv_intPlayer)))) {
                        lv_boolValidCommand = true;
                        lv_intTmpPlayer = StringToInt(StringSub(lv_strChatString, 8, 10));
                        gf_CreateChatMessage(0, (TextWithColor(PlayerName(lv_intPlayer), libNtve_gf_ConvertPlayerColorToColor(PlayerGetColorIndex(lv_intPlayer, false))) + StringExternal("Param/Value/225B5472")), lv_intTmpPlayer, false, false);
                        gf_CreateChatMessage(0, (TextWithColor(PlayerName(lv_intTmpPlayer), libNtve_gf_ConvertPlayerColorToColor(PlayerGetColorIndex(lv_intTmpPlayer, false))) + StringExternal("Param/Value/06C76FC6")), lv_intTmpPlayer, false, false);
                    }
                    else if ((((StringSub(lv_strChatString, 1, 8) == ".ignore ") && (StringToInt(StringSub(lv_strChatString, 9, 11)) < 17) && (StringToInt(StringSub(lv_strChatString, 9, 11)) > 0)))) {
                        lv_boolValidCommand = true;
                        lv_intTmpPlayer = StringToInt(StringSub(lv_strChatString, 9, 11));
                        gv_recPlayers[lv_intPlayer].lv_intPlayerIsIgnored[lv_intTmpPlayer] = true;
                        gf_CreateChatMessage(0, (StringExternal("Param/Value/57E308B4") + TextWithColor(PlayerName(lv_intTmpPlayer), libNtve_gf_ConvertPlayerColorToColor(PlayerGetColorIndex(lv_intTmpPlayer, false)))), lv_intTmpPlayer, false, false);
                    }
                    else if ((((StringSub(lv_strChatString, 1, 10) == ".unignore ") && (StringToInt(StringSub(lv_strChatString, 11, 13)) < 17) && (StringToInt(StringSub(lv_strChatString, 11, 13)) > 0)))) {
                        lv_boolValidCommand = true;
                        lv_intTmpPlayer = StringToInt(StringSub(lv_strChatString, 11, 13));
                        gv_recPlayers[lv_intPlayer].lv_intPlayerIsIgnored[lv_intTmpPlayer] = false;
                        gf_CreateChatMessage(0, (StringExternal("Param/Value/B39C76F1") + TextWithColor(PlayerName(lv_intTmpPlayer), libNtve_gf_ConvertPlayerColorToColor(PlayerGetColorIndex(lv_intTmpPlayer, false)))), lv_intTmpPlayer, false, false);
                    }
                    else if ((lv_strChatString == ".disablef11")) {
                        lv_boolValidCommand = true;
                        gv_recPlayers[lv_intPlayer].lv_boolDisableF11 = true;
                        gf_CreateChatMessage(0, StringExternal("Param/Value/BC71716A"), lv_intPlayer, false, false);
                    }
                    if ((lv_boolValidCommand == false)) {
                        gf_CreateChatMessage(0, StringExternal("Param/Value/FD627A67"), lv_intPlayer, false, false);
                    }

                }
                else {
                    if (((gv_recPlayers[lv_intPlayer].lv_boolPlayingMatch == true) && (gv_recGameState.lv_intCurrentGameState == 3) && (StringCase(lv_strChatString, false) == "gg"))) {
                        DialogSetVisible(gv_recUI.lv_dSurrenderDialog, PlayerGroupSingle(lv_intPlayer), true);
                    }
                    else {
                        if (((gv_recPlayers[lv_intPlayer].lv_boolPlayingMatch == true) && (gv_recGameState.lv_intCurrentGameState == 3) && (StringCase(lv_strChatString, false) == "ggs"))) {
                            lv_boolSpecialCommandEntered = true;
                            DialogSetVisible(gv_recUI.lv_dSurrenderDialog, PlayerGroupSingle(lv_intPlayer), true);
                        }

                    }
                }
            }
            if ((lv_boolSpecialCommandEntered == false)) {
                lv_txtMessage = StringToText(gf_replaceSpecialChars(lv_strChatString));
                gf_CreateChatMessage(lv_intPlayer, lv_txtMessage, 0, false, false);
            }

        }
    }

    return true;
}

