//--------------------------------------------------------------------------------------------------
bool gt_KeyAltJMapAnalysis_Func (bool testConds, bool runActions) {
    // Variable Declarations
    int lv_intPlayer;

    // Automatic Variable Declarations
    // Variable Initialization
    lv_intPlayer = EventPlayer();

    // Conditions
    if (testConds) {
        if (!(((gv_recGameState.lv_intGameStyle == 2) && (gv_recPlayers[lv_intPlayer].lv_boolPlayingMatch == false) && ((gv_recPlayers[lv_intPlayer].lv_boolIsCommentator == true) || (gv_recGlobals.lv_intDebugPlayer == lv_intPlayer))))) {
            return false;
        }
    }

    // Actions
    if (!runActions) {
        return true;
    }

    if ((DialogIsVisible(gv_recUI.lv_dSketchDialog, lv_intPlayer) == true)) {
        if ((gv_recPlayers[lv_intPlayer].lv_boolMapAnalysisEngaged == false)) {
            gv_recPlayers[lv_intPlayer].lv_boolMapAnalysisEngaged = true;
            libNtve_gf_SetDialogItemImage(gv_recUI.lv_diSketchMapImage, "Assets\\Textures\\_base_extrastuff_col.dds", PlayerGroupAll());
            gf_HideTownhallIconsforPlayer(lv_intPlayer);
        }
        else {
            gf_DisengageSketchpadforPlayer(lv_intPlayer);
        }
    }
    else {
        gv_recPlayers[lv_intPlayer].lv_boolMapAnalysisEngaged = true;
        libNtve_gf_SetDialogItemImage(gv_recUI.lv_diSketchMapImage, "Assets\\Textures\\_base_extrastuff_col.dds", PlayerGroupAll());
        gf_EngageSketchpadforPlayer(lv_intPlayer, true);
        gf_HideTownhallIconsforPlayer(lv_intPlayer);
    }
    return true;
}

