    // Conditions
    if (testConds) {
        if (!(((gv_recGameState.lv_intCurrentGameState == 3) && (TimerGetRemaining(gv_recGlobals.lv_timerResourceTrading) <= 0.0)))) {
            return false;
        }
    }

    // Actions
    if (!runActions) {
        return true;
    }

    lv_intPlayer2 = gv_recPlayers[lv_intPlayer].lv_intTeammateList[DialogControlGetSelectedItem(gv_recUI.lv_diResourcePlayerPulldown, lv_intPlayer)];
    lv_intNumMineralsToSend = gv_recPlayers[lv_intPlayer].lv_intNumMineralsToTrade;
    if ((lv_intNumMineralsToSend > PlayerGetPropertyInt(lv_intPlayer, c_playerPropMinerals))) {
        lv_intNumMineralsToSend = PlayerGetPropertyInt(lv_intPlayer, c_playerPropMinerals);
    }

    lv_intNumVespeneToSend = gv_recPlayers[lv_intPlayer].lv_intNumMineralsToTrade;
    if ((lv_intNumVespeneToSend > PlayerGetPropertyInt(lv_intPlayer, c_playerPropVespene))) {
        lv_intNumVespeneToSend = PlayerGetPropertyInt(lv_intPlayer, c_playerPropVespene);
    }

    if (((gv_recPlayers[lv_intPlayer].lv_boolPlayingMatch == true) && (gv_recPlayers[lv_intPlayer2].lv_boolPlayingMatch == true))) {
        PlayerModifyPropertyInt(lv_intPlayer, c_playerPropMinerals, c_playerPropOperSubtract, lv_intNumMineralsToSend);
        PlayerModifyPropertyInt(lv_intPlayer, c_playerPropVespene, c_playerPropOperSubtract, lv_intNumVespeneToSend);
        PlayerModifyPropertyInt(lv_intPlayer2, c_playerPropMinerals, c_playerPropOperAdd, lv_intNumMineralsToSend);
        PlayerModifyPropertyInt(lv_intPlayer2, c_playerPropVespene, c_playerPropOperAdd, lv_intNumVespeneToSend);
    }

    gv_recPlayers[lv_intPlayer].lv_intNumMineralsToTrade = 0;
    gv_recPlayers[lv_intPlayer].lv_intNumVespeneToTrade = 0;
    gf_RefreshResourceTradingDialogforPlayer(lv_intPlayer);
    DialogSetVisible(gv_recUI.lv_dResourceTradingDialog, PlayerGroupSingle(lv_intPlayer), false);
    return true;
